import React, { useState, setState } from "react";
import styled from "styled-components";

// A div is a division that can be used as a container in the html
// This is a styled div we named container. Below it is just regular CSS.
const Container = styled.div`
  border-radius: 3px;
  width: 100px;
  height: 110px;
  margin: 0 auto;
  background-color: #eee;
  background-clip: border-box;
  border: none;
  border-radius: 0.5rem;
  border-color: #c4c4c4;
  box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075),
    0 0 8px hsla(0, 0%, 76.9%, 0.4);
  margin-top: 20px;
`;

// Here is a styled div we named Count.
const Count = styled.div`
  color: #337067;
  font-size: 55px;
  display: flex;
  justify-content: center;
  width: 100%;
`;

// And a styled button tag we named Button.
const Button = styled.button`
  background: #337067;
  border-radius: 3px;
  color: white;
  display: block;
  margin-left: auto;
  margin-right: auto;
  width: 70%;
`;

// This Counter function is a component that is exported from here and
// imported into index.js to be rendered inside the <App/> component.
const inBox = document.querySelector(".inputBox")
export default function MadLibComp() {
  const [index, setIndex] = useState(1)
  const tester = "Tester"
  var [userValues, setVal] = useState({
    1:{
      'value': "",
      'name': " Noun",
      'ex': 'Larry'
    },
    2:{
      'value': "",
      'name': "nother Noun",
      'ex': 'Italy'
    },
    3:{
      'value': "",
      'name': " Verb",
      'ex': 'Adjective'
    },
    4:{
      'value': "",
      'name': " Adjective",
      'ex': 'Cool'
    }
  });

function placeChange(){

  if(index==4){
    setIndex(1)
    inBox.value = ""
  }
  else{
    const inBox = document.querySelector(".inputBox")
  console.log(userValues)
  setIndex(index+1)
  inBox.value = ""
  console.log(index)


  }
}

  return (
    <div class="text-center">
      <div id="insertVal">Type a{userValues[index].name}<br></br>{userValues[index].value}</div>
    <input class="inputBox" onChange={e => setVal({...userValues, index: {'value': e.target.value}})} type="text" name="firstName" placeholder={userValues[index].ex}/>
    <br></br><br></br>
    <button onClick={placeChange}>Next</button>
    </div>
  );
}
