import React, { useState } from "react";
import styled from "styled-components";

// A div is a division that can be used as a container in the html
// This is a styled div we named container. Below it is just regular CSS.
const Container = styled.div`
  border-radius: 3px;
  width: 100px;
  height: 110px;
  margin: 0 auto;
  background-color: #eee;
  background-clip: border-box;
  border: none;
  border-radius: 0.5rem;
  border-color: #c4c4c4;
  box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075),
    0 0 8px hsla(0, 0%, 76.9%, 0.4);
  margin-top: 20px;
`;

// Here is a styled div we named Count.
const Count = styled.div`
  color: #337067;
  font-size: 55px;
  display: flex;
  justify-content: center;
  width: 100%;
`;

// And a styled button tag we named Button.
const Button = styled.button`
  background: #337067;
  border-radius: 3px;
  color: white;
  display: block;
  margin-left: auto;
  margin-right: auto;
  width: 70%;
`;

// This Counter function is a component that is exported from here and
// imported into index.js to be rendered inside the <App/> component.

export default function MadLibComp() {
  const userValues = [
    {
      'value': "",
      'name': " Noun",
      'ex': 'Larry'
    },
    {
      'value': "",
      'name': "nother Noun",
      'ex': 'Larry'
    },
    {
      'value': "",
      'name': " Verb",
      'ex': 'Adjective'
    },
    {
      'value': "",
      'name': " Noun",
      'ex': 'Larry'
    }
  ]

  const [index, setVal] = useState(0);
  function handleChange(e) {
    const inBox = document.querySelector(".inputBox")
    inBox.value = e;
    console.log(e)
  }
  const placeChange = () => {
    const inBox = document.querySelector(".inputBox")
    if(index==3){
      setVal(0)
      inBox.value = "Done"

    }
    else{
    setVal(index+1)
    }

  }

  return (
    <div class="text-center">
      <div id="insertVal">Type a{userValues[index].name}</div>
    <input class="inputBox" defaultValue="" type="text" name="firstName" onChange={this.onChange} placeholder={userValues[index].ex}/>
    <br></br><br></br>
    <button onClick={placeChange}>Next</button>
    </div>
  );
}
